std::string markdownToHTML(const std::string& markdownText) {
    // Define the grammar for parsing the markdown text
    namespace qi = boost::spirit::qi;
    namespace phoenix = boost::phoenix;
    auto header = qi::lexeme[+qi::alpha >> *(qi::alpha | qi::digit |'' | '_')];
    auto link = qi::phrase_match[qi::double_string("[", "](", qi::repeat(1)[header >> qi::lit(",") >> header]) >> qi::lit(")")];
    auto url = qi::string("https?://[^()]*");
    auto blockQuote = qi::string("> ") >> *(qi::space >> +(qi::graph - '>'));
    auto codeBlock = qi::string("```") >> *(qi::graph - qi::string("```")) >> qi::string("```");
    auto listItem = qi::string("- ") >> *(qi::graph - '\n');
    auto bulletList = qi::repeat(1)[listItem >> *(qi::space >> listItem)];
    auto numberedList = qi::repeat(1)[qi::int_ >> qi::string(". ") >> *(qi::graph - '\n')];
    auto paragraph = qi::repeat(1)[+(qi::graph - '\n') >> qi::space];
    auto text = qi::repeat(1)[+(qi::graph - '\n') >> qi::space];

    // Define the function to convert markdown to HTML
    return boost::spirit::ascii::phrase_parse(markdownText.cbegin(), markdownText.cend(),
        (header
            | link
            | url
            | blockQuote
            | codeBlock
            | bulletList
            | numberedList
            | paragraph
            | text
        ),
        boost::spirit::ascii::space
    )? "Parsing failed" : "Parsing succeeded";
}

