#include <stdio.h>
#include <stdlib.h>
#include <SDL2/SDL.h>

#define SCREEN_WIDTH  640
#define SCREEN_HEIGHT 480

// structure to hold the snake's head position
typedef struct {
    int x;
    int y;
} SnakeHead;

// function to initialize game resources
void initGame(SDL_Renderer *renderer) {
    // initialize SDL
    SDL_Init(SDL_INIT_VIDEO);

    // create window
    SDL_Window *window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_FULLSCREEN);

    // create renderer
    renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
}

// function to load image
SDL_Texture *loadImage(const char *filePath, SDL_Renderer *renderer) {
    // load image
    SDL_Surface *surface = SDL_LoadBMP(filePath);

    // create texture from surface
    SDL_Texture *texture = SDL_CreateTextureFromSurface(renderer, surface);

    // free surface
    SDL_FreeSurface(surface);

    return texture;
}

// function to render the game
void renderGame(SDL_Renderer *renderer, SnakeHead snakeHead, SDL_Texture *appleTexture) {
    // set background color
    SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);

    // clear screen
    SDL_RenderClear(renderer);

    // render apple
    SDL_Rect appleRect = {snakeHead.x * 32, snakeHead.y * 32, 32, 32};
    SDL_RenderCopy(renderer, appleTexture, NULL, &appleRect);

    // update screen
    SDL_RenderPresent(renderer);
}

int main(int argc, char *argv[]) {
    // initialize game resources
    SDL_Renderer *renderer = NULL;
    initGame(renderer);

    // load images
    SDL_Texture *appleTexture = loadImage("apple.bmp", renderer);

    // initialize snake head position
    SnakeHead snakeHead = {0, 0};

    // game loop
    bool quit = false;
    SDL_Event event;
    while (!quit) {
        // handle events
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                quit = true;
            }
        }

        // update game state
        snakeHead.x++;

        // render game
        renderGame(renderer, snakeHead, appleTexture);
    }

    // free resources
    SDL_DestroyTexture(appleTexture);
    SDL_DestroyRenderer(renderer);
    SDL_Quit();

    return 0;
}
```
