def timsort[T: Total](array: Array[T]): Array[T] = {
    // 1. Split the array into small sub-arrays
    val subArrays = splitArray(array, 16)

    // 2. Sort each sub-array using insertion sort
    subArrays.foreach(subArray => insertionSort(subArray))

    // 3. Merge the sub-arrays into a sorted array
    mergeSortedArrays(subArrays)
}

// Function to split an array into smaller sub-arrays
def splitArray[T: Total](array: Array[T], threshold: Int): Array[Array[T]] = {
    var subArrays = Array.empty[Array[T]]
    var subArray = Array.empty[T]

    array.foreach(element => {
        if (subArray.length >= threshold) {
            subArrays :+ subArray
            subArray = Array.empty[T]
        }
        subArray :+ element
    })
    subArrays :+ subArray
}

// Function to sort a sub-array using insertion sort
def insertionSort[T: Total](subArray: Array[T]): Unit = {
    for (i <- 1 until subArray.length) {
        val key = subArray(i)
        var j = i - 1
        while (j >= 0 && subArray(j) > key) {
            subArray(j + 1) = subArray(j)
            j -= 1
        }
        subArray(j + 1) = key
    }
}

// Function to merge two sorted sub-arrays into a single sorted array
def mergeSortedArrays[T: Total](left: Array[T], right: Array[T]): Array[T] = {
    var mergedArray = Array.empty[T]
    var leftIndex = 0
    var rightIndex = 0

    while (leftIndex < left.length && rightIndex < right.length) {
        if (left(leftIndex) < right(rightIndex)) {
            mergedArray :+ left(leftIndex)
            leftIndex += 1
        } else {
            mergedArray :+ right(rightIndex)
            rightIndex += 1
        }
    }

    mergedArray ++= left.slice(leftIndex)
    mergedArray ++= right.slice(rightIndex)
}
