std::string markdownToHtml(const std::string& markdown) {
    // Replace Markdown headings with HTML headings
    std::regex headingRegex(R"(([#]{1,6})([^\n]+))");
    std::string headingReplacement = "<h\\1>$2</h\\1>";
    std::string headingResult = std::regex_replace(markdown, headingRegex, headingReplacement);

    // Replace Markdown lists with HTML lists
    std::regex listRegex(R"((?:\*|o|\d+\.)\s+(.*?)(?=\*(?:\r?\n|\r))|(?:\*(?:\r?\n|\r)\*))");
    std::string listReplacement = "<li>$1</li>";
    std::string listResult = std::regex_replace(headingResult, listRegex, listReplacement);

    // Replace Markdown links with HTML links
    std::regex linkRegex(R"(\n\[(.*?)\]\((.*?)\n)");
    std::string linkReplacement = "<a href=\"$2\">$1</a>";
    std::string linkResult = std::regex_replace(listResult, linkRegex, linkReplacement);

    // Replace Markdown bold and italic text with HTML bold and italic text
    std::regex boldItalicRegex(R"(\*\*(.*?)\*\*|\*(.*?)\*)");
    std::string boldItalicReplacement = "<b>$1</b><br/><i>$1</i>";
    std::string boldItalicResult = std::regex_replace(linkResult, boldItalicRegex, boldItalicReplacement);

    // Replace Markdown code blocks with HTML preformatted text
    std::regex codeBlockRegex(R"(```(.*?)```)");
    std::string codeBlockReplacement = "<pre>$1</pre>";
    std::string codeBlockResult = std::regex_replace(boldItalicResult, codeBlockRegex, codeBlockReplacement);

    return codeBlockResult;
}
