#include <string>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;

    // Iterate through each character in the markdown string
    for (char c : markdown) {
        // Check if the character is a hash symbol (#)
        if (c == '#') {
            // If it is, append an opening HTML header tag
            html += "<h1>";
        }
        // Check if the character is an asterisk (\*)
        else if (c == '*') {
            // If it is, append an opening HTML strong tag
            html += "<strong>";
        }
        // Append the character to the HTML string
        html += c;
        // Check if the character is a newline
        if (c == '\n') {
            // Append a closing HTML tag for the previous element
            html.pop_back();
            html += "</";
            // Check if the previous element was a header
            if (html.substr(html.size() - 5) == "h1>") {
                // If it was, append a closing HTML header tag
                html += "1>";
            }
            // Check if the previous element was a strong element
            else if (html.substr(html.size() - 9) == "strong>") {
                // If it was, append a closing HTML strong tag
                html += "strong>";
            }
            // Append a newline character to the HTML string
            html += "<br>";
        }
    }

    // Append a closing HTML tag for the last element
