```cpp
#include <string>
#include <regex>
#include <iostream>

std::string markdownToHtml(const std::string& markdown) {
    std::string html;

    // Replace links
    std::regex linkRegex("!\[(.*?)\]\\((.*?)\\)");
    std::regex_replace(std::back_inserter(html), markdown.begin(), markdown.end(), linkRegex,
                       [](const std::smatch& match) {
                           return "<a href=\"" + match.str(2) + "\">" + match.str(1) + "</a>";
                       });

    // Replace headers
    std::regex headerRegex("(#+\\s*(.*?))");
    std::regex_replace(std::back_inserter(html), markdown.begin(), markdown.end(), headerRegex,
                       [](const std::smatch& match) {
                           std::string level = std::string(match.str(1).length(),'');
                           return "<" + level + ">" + match.str(2) + "</" + level + ">";
                       });

    // Replace lists
    std::regex listRegex("(\\*|\\d+\\.)\\s*(.*)");
    std::regex_replace(std::back_inserter(html), markdown.begin(), markdown.end(), listRegex,
                       [](const std::smatch& match) {
                           return "<li>" + match.str(3) + "</li>";
                       });

    // Replace code blocks
    std::regex codeBlockRegex("```(.*?)```");
    std::regex_replace(std::back_inserter(html), markdown.begin(), markdown.end(), codeBlockRegex,
                       [](const std::smatch& match) {
                           return "<pre><code>" + match.str(1) + "</code></pre>";
                       });

    // Replace bold and italic text
    std::regex boldItalicRegex("(\*\*(.*?)\*\*|\\*(.*?)\*)");
    std::regex_replace(std::back_inserter(html), markdown.begin(), markdown.end(), boldItalicRegex,
                       [](const std::smatch& match) {
                           if (match.str(1).length() > 0) {
               return "<b>" + match.str(1) + "</b>";
                           } else {
               return "<i>" + match.str(2) + "</i>";
                           }
                       });

    return html;
}

