class Queue:
    def __init__(self):
        self.in_stack = []
        self.out_stack = []

    def push(self, value):
        self.in_stack.append(value)

    def pop(self):
        if not self.out_stack:
            while self.in_stack:
                self.out_stack.append(self.in_stack.pop())
        return self.out_stack.pop()

    def peek(self):
        if not self.out_stack:
            while self.in_stack:
                self.out_stack.append(self.in_stack.pop())
        return self.out_stack[-1]

    def size(self):
        return len(self.in_stack) + len(self.out_stack)
```
