const { REST } = require("@discordjs/rest");
const { Routes } = require("discord.js");
const fs = require("fs");
const path = require("path");
const https = require("https");

const client = new Client({ intents: [
  "Guilds",
  "GuildBans",
  "GuildMessages",
  "MessageContent"
] });

