function checkPrimes(arr) {
  const primes = [];

  for (let i = 0; i < arr.length; i++) {
    const num = arr[i];

    // Check if the number is not 1, and not divisible by any numbers except 1 and itself
    if (num > 1 &&!isPrime(num)) {
